outerLoop:		//creating label
for(i=1;i<10;i++)
{
	log.info "i am into outer for loop";
	for(j=1;j<5;j++)
	{
		log.info "i am into INNER for loop";

		if(j==3)
		{
			break outerLoop; //breaking label. this will come out of not only inner loop but also outer loop
		}
	}
}

