			 //ResposeValueText = ParsedJSONResponseInString.substring(IndexOfParam+LengthOfParam+2,IndexOfSubsequentComma-1); 
				log.info "				>>>>>>>>>>>>>>>>>> : ResposeValueText here :"+ResposeValueText;

